insert into Publicação(Titulo,CorpoNoticia,DtaPublicacao,idCategoria,idArea) Select "Testando o Titulo","Testando a publicação",DAY(GETDATE()), MONTH(GETDATE()), YEAR(GETDATE()),CONVERT (time, SYSDATETIME()), Select idCategoria from Categoria Where Exists idCategoria = 1,Select idArea from Area where idArea = 1